import React, { useState, useEffect } from "react";

// import all the components we are going to use
import {
  SafeAreaView,
  Text,
  StyleSheet,
  View,
  FlatList,
  TextInput,
  Image,
  ImageBackground,
  ActivityIndicator,
  Alert,
} from "react-native";

import Icon from "react-native-vector-icons/FontAwesome";
import { api } from "../../api";
import axios from "axios";
const Product = ({ route,navigation }) => {
  const { itemId } = route.params;
  const { typeID } = route.params;

  const [isLoading, setIsLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [filteredDataSource, setFilteredDataSource] = useState([]);
  const [masterDataSource, setMasterDataSource] = useState([]);
  const [datauser, setDatauser] = useState([]);
  const [dataTT, setDataTT] = useState("");

  useEffect(async () => {
    await axios.get(api + "/user/" + itemId).then((result) => {
      setDatauser(result.data[0].data[0].user_grade);
    });
    fetch(api + "/productcate/" + typeID)
      .then((response) => response.json())
      .then((responseJson) => {
        setFilteredDataSource(responseJson);
        setMasterDataSource(responseJson);
        setDataTT(responseJson[0].cate_name);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [typeID]);
  // console.log(datauser);
  const searchFilterFunction = (text) => {
    // Check if searched text is not blank
    if (text) {
      // Inserted text is not blank
      // Filter the masterDataSource
      // Update FilteredDataSource
      const newData = masterDataSource.filter(function (item) {
        const itemData = item.product_name
          ? item.product_name.toUpperCase()
          : "".toUpperCase();
        const textData = text.toUpperCase();
        return itemData.indexOf(textData) > -1;
      });
      setFilteredDataSource(newData);
      setSearch(text);
    } else {
      // Inserted text is blank
      // Update FilteredDataSource with masterDataSource
      setFilteredDataSource(masterDataSource);
      setSearch(text);
    }
  };

  const ItemView = ({ item }) => {
    const box = (price) => {
      return (
        <View
          style={styles.boxItems}
          onPress={() => {
            seletedata(item.product_name, item.product_id, item.product_price);
          }}
        >
          {/* https://www.ผักสดเชียงใหม่.com/image/product_image/676.jpg */}
          <ImageBackground
            source={{
              uri: `https://hotel78maldives.com/thecatch/wp-content/uploads/2021/03/NoImageAvailable.png`,
            }}
            resizeMode="cover"
            style={{ flex: 1, justifyContent: "center" }}
          >
            <Image
              source={{
                uri: `https://www.ผักสดเชียงใหม่.com/image/product_image/${item.product_id}.jpg`,
              }}
              style={{ width: 180, height: 180 }}
            />
          </ImageBackground>
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginTop: 1,
              position: "relative",
            }}
          >
            <Text style={styles.itemStyle} onPress={() => getItem(item)}>
              {item.product_name.toUpperCase()}{" "}
            </Text>
            <View
              style={{
                marginRight: 20,
                position: "absolute",
                right: 0,
                top: 18,
              }}
            >
              <Icon
                name="shopping-cart"
                size={25}
                color="#029c0f"
                onPress={() => {
                  seletedata(
                    item.product_name,
                    item.product_id,
                    item.price_id,
                    item.product_amount
                  );
                }}
              />
            </View>
          </View>
          <Text
            style={{
              marginLeft: 5,
              alignSelf: "flex-start",
              fontSize: 13,
              margin: 2,
            }}
          >
            คงเหลือ{" "}
            {item.product_amount == null || item.product_amount == 0 ? (
              <Text style={{ color: "#750e0b" }}> สินค้าหมด</Text>
            ) : (
              item.product_amount
            )}
          </Text>
          <View
            style={{
              display: "flex",
              flexDirection: "row",
            }}
          >
            <Text style={{ flexGrow: 1, marginLeft: 5, fontSize: 13 }}>
              หน่วย ( 1 {item.unit_name} )
            </Text>
            <Text style={{ marginRight: 5, color: "#054d05" }}>
              {price}
              {" บาท "}
            </Text>
          </View>
        </View>
      );
    };
    if (datauser == "D") {
      return box(item.price_sell_D);
    } else if (datauser == "C") {
      console.log("C");
      return box(item.price_sell_C);
    } else if (datauser == "B") {
      console.log("B");
      return box(item.price_sell_B);
    } else if (datauser == "A") {
      console.log("A");
      return box(item.price_sell_A);
    }
  };

  const ItemSeparatorView = () => {
    return (
      // Flat List Item Separator
      <View
        style={{
          height: 0.5,
          width: "30%",
          // backgroundColor: "#C8C8C8",
        }}
      />
    );
  };

  const getItem = (item) => {
    // Function for click on an item
    alert("Id : " + item.id + " Title : " + item.title);
  };
  const seletedata = async (proname, product_id, price_id, amount) => {
    var grade = "D";
    if (datauser == "D") {
      grade = "D";
    } else if (datauser == "C") {
      grade = "C";
    } else if (datauser == "B") {
      grade = "B";
    } else if (datauser == "A") {
      grade = "A";
    }

    
    console.log("amount = " + amount);
    if (amount == null || amount == 0) {
      Alert.alert(
        `ไม่สามารถเพิ่ม${proname}ได้ !!`,
        "สิ้นค้าหมดชั่วคราว กรุณาเลือกสินค้าอื่น",
        [
          {
            text: "ยืนยัน",
          },
        ]
      );
    } else {
      await axios
        .post(api + "/select-product", {
          id: itemId,
          product_id: product_id,
          price_id: price_id,
          pro_req_amount: 1,
          grade: grade,
        })
        .then(function (response) {
          console.log(response.data.result);
          if (response.data.result == "ok") {
            Alert.alert(
              `เพิ่มสินค้า ${proname} สำเร็จ !!`,
              "คุณสามารถเพิ่มจำนวนสินค้าที่ในเมนู Order",
              [
                {
                  text: "ยืนยัน",
                  onPress: () => {
                    console.log(itemId);
                    console.log(product_id);
                    console.log(price_id);
                  },
                },
              ]
            );
          } else {
            Alert.alert(
              `สินค้า ${proname} ถูกเพิ่มไปในตะกล้าแล้ว `,
